        -- Rating SQL from author @LuxLait (Frisch Tim)
 
CREATE TABLE tblRating(
    idRating DOUBLE NOT NULL,
    dtDescription VARCHAR(15),
    dtImage VARCHAR(30),
    PRIMARY KEY (idRating)
);

VALUES (0,'Empty Star','bi bi-star'),
        (0.5,'Half Star','bi bi-star-half'),
