        -- Product rating SQL from author @LuxLait (Frisch Tim)

CREATE TABLE tblProductRating(
    idProductRating INT NOT NULL AUTO_INCREMENT,
    fiRating DOUBLE NOT NULL,
    fiProduct INT NOT NULL,
    fiUsername VARCHAR(10) NOT NULL,
    PRIMARY KEY (idProductRating),
    FOREIGN KEY (fiRating) REFERENCES tblRating (idRating) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (fiProduct) REFERENCES tblProduct (idProduct) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (fiUsername) REFERENCES tblUser (idUsername) ON DELETE CASCADE ON UPDATE CASCADE
);
        

        -- Recipe SQL from autor @LuxKeis (Gamass Mihails)

CREATE TABLE tblRecipe(
    idRecipe INT NOT NULL AUTO_INCREMENT,
    dtName VARCHAR(100) NOT NULL,
    dtDescription VARCHAR(300) NOT NULL,
    dtImage VARCHAR(225) NOT NULL,
    PRIMARY KEY (idRecipe)
);
 
 
 
-- Ingredient SQL from autor @LuxKeis (Gamass Mihails)

CREATE TABLE tblIngredient(
    idIngredient INT NOT NULL AUTO_INCREMENT,
    dtMetricUnit VARCHAR(25) NOT NULL,
    dtIngredient VARCHAR(100) NOT NULL,
    fiRecipe INT NOT NULL,
    PRIMARY KEY (idIngredient),
    FOREIGN KEY (fiRecipe) REFERENCES tblRecipe (idRecipe) ON DELETE CASCADE ON UPDATE CASCADE
);

 
 
-- Recipe steps SQL from autor @LuxKeis (Gamass Mihails)

CREATE TABLE tblRecipeStep(
    idRecipeStep INT NOT NULL AUTO_INCREMENT,
    dtStep VARCHAR(800) NOT NULL,
    fiRecipe INT NOT NULL,
    PRIMARY KEY (idRecipeStep),
    FOREIGN KEY (fiRecipe) REFERENCES tblRecipe (idRecipe) ON DELETE CASCADE ON UPDATE CASCADE
);


-- Shoppingcart SQL from autor @Forlu102 (Formica Luca)
 
    CREATE TABLE tblShoppingCart(
        idCart INT NOT NULL AUTO_INCREMENT,
        dtAmount INT NOT NULL,
        fiProduct INT NOT NULL,
        fiUsername VARCHAR(10) NOT NULL,
        PRIMARY KEY (idCart),
        FOREIGN KEY (fiProduct) REFERENCES tblProduct (idProduct) ON DELETE CASCADE ON UPDATE CASCADE,
        FOREIGN KEY (fiUsername) REFERENCES tblUser (idUsername) ON DELETE CASCADE ON UPDATE CASCADE
    );


-- Payment SQL from autor @LuxReis (GUO Kaidi)
 
    CREATE TABLE tblPayment(
        idPayment INT NOT NULL AUTO_INCREMENT,
        dtDate DATETIME NOT NULL,
        dtTotalPrice DOUBLE NOT NULL,
        dtStatus VARCHAR(10) NOT NULL,
        PRIMARY KEY (idPayment)
    );



-- Many to Many with tblShoppingCart and tblPayment SQL from autor @LuxReis (GUO Kaidi)

    CREATE TABLE tblHave(
        fiCart INT NOT NULL,
        fiPayment INT NOT NULL,
        PRIMARY KEY (fiCart,fiPayment),
        FOREIGN KEY (fiCart) REFERENCES tblShoppingCart (idCart) ON DELETE CASCADE ON UPDATE CASCADE,
        FOREIGN KEY (fiPayment) REFERENCES tblPayment (idPayment) ON DELETE CASCADE ON UPDATE CASCADE
    );

 
-- Delievery SQL from autor @LuxReis (GUO Kaidi)
 
    CREATE TABLE tblDelivery(
        idDelivery INT NOT NULL AUTO_INCREMENT,
        dtStatus VARCHAR(20) NOT NULL,
        fiPayment INT NOT NULL,
        PRIMARY KEY (idDelivery),
        FOREIGN KEY (fiPayment) REFERENCES tblPayment (idPayment) ON DELETE CASCADE ON UPDATE CASCADE
    );
